mapper.select:
select *
from board_file
where f_no = #{fNO}

mapper.update:
update board
set b_title = #{bTitle}, b_content = #{bContent}, b_critical = #{bCritical}
where b_no = #{bNo}
and b_status = 'N'

update board
set b_status = 'Y'
where b_no = #{bNo}

mapper.select:
select count(*) from board where b_status = 'N' and b_type = #{type}
and b_title like '%' || #{value} || '%'
and b_content like '%' || #{value} || '%'

select b_no, b_title, b_content, b_date, b_count, b_type, b_writer, name, category_no, category_name, b_status, f_no, f_url, f_name, f_rname from board join member on(b_writer = m_no) join category using(category_no) left join board_file using(b_no) where b_type = 'COMMON' and b_status = 'N'
and category_no = #{value}
order by b_no desc

mapper.update:
update board
set b_title = #{bTitle}, b_content = #{bContent}, category_no = #{categoryNo}
where b_no = #{bNo}

update reply
set r_status = 'Y'
where r_no = #{rNo}

update board
set b_title = #{bTitle}, b_content = #{bContent}
where b_no = #{bNo}
and b_status = 'N'

update board
set b_status = 'Y'
where b_no = #{bNo}
