mapper.update:
UPDATE MAIL_FILE
SET ML_STATUS = 'N'
WHERE M_FILE_NO=#{mFileNo}

update.if:
update mail_s_r
set s_r_status='N'
where mailno=#{mailNo}
and MNO=#{mNo}

update MAIL
set estatus='D'
where mailno=#{mailNo}
and MNO=#{mNo}
and estatus='T'

mapper.update:

update mail_s_r
set r_date = sysdate
where mailno = #{id}	
and mno = #{mNo}

UPDATE MAIL
SET
	RECEIVEEMP = #{receiveEmp},
	ETITLE = #{etitle},
	ECONTENT = #{econtent}
where mailNo = #{mailNo}

UPDATE MAIL_S_R
SET favorites = #{favorites}
where mno = #{mNo}
and mailno = #{id}
